import Color from 'color-js';

var COLORS = [
    "2,63,165",
    "142,6,59",
    "74,111,227",
    "211,63,106",
    "17,198,56",
    "239,151,8",
    "15,207,192",
    "247,156,212",
    "125,135,185",
    "187,119,132",
    "133,149,225",
    "224,123,145",
    "141,213,147",
    "240,185,141",
    "156,222,214",
    "246,196,225",
    "190,193,212",
    "214,188,192",
    "181,187,227",
    "230,175,185",
    "198,222,199",
    "234,211,198",
    "213,234,231",
    "243,225,235"
];

COLORS.getColor = function(index, alpha) {
    var base = COLORS[index];
    var color = Color("rgb(" + base + ")");
    return color.lightenByRatio(1 - alpha).toRGB();
};

export default COLORS;